import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import DashboardPage from './DashboardPage';
import AddEmployeePage from './AddEmployeePage';
import UpdateEmployeePage from './UpdateEmployeePage';
import DeleteEmployeePage from './DeleteEmployeePage';
import EmployeeDetailsPage from './EmployeeDetailsPage';

function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = () => {
        if (username === 'gowtham' && password === '123') {
            navigate('/dashboard');
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Login</button>
        </div>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<LoginPage />} />
                <Route path="/dashboard" element={<DashboardPage />} />
                <Route path="/add-employee" element={<AddEmployeePage />} /> {/* Add the route */}
                <Route path="/update-employee/:id" element={<UpdateEmployeePage />} /> {/* Add the route */}
                <Route path="/delete-employee/:id" element={<DeleteEmployeePage />} /> {/* Add the route */}
                <Route path="/employee/:id" element={<EmployeeDetailsPage />} /> {/* Add missing angle bracket */}
            </Routes>
        </Router>
    );
}

export default App;
