import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';


function AddEmployeePage() {
    const [employeeData, setEmployeeData] = useState({
        first_name: '',
        last_name: '',
        department: '',
        salary: '',
        DOB: '',
        profile: null, 
    });

    const navigate = useNavigate();

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setEmployeeData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        setEmployeeData((prevData) => ({
            ...prevData,
            profile: file,
        }));
    };

    const handleAddEmployee = () => {
        if (
            !employeeData.first_name ||
            !employeeData.last_name ||
            !employeeData.department ||
            !employeeData.salary ||
            !employeeData.DOB ||
            !employeeData.profile
        ) {
            alert('All fields (first_name, last_name, department, salary, DOB, and profile image) are required.');
            return;
        }

        const formattedDOB = new Date(employeeData.DOB).toISOString().split('T')[0];

        const formData = new FormData();
        formData.append('first_name', employeeData.first_name);
        formData.append('last_name', employeeData.last_name);
        formData.append('department', employeeData.department);
        formData.append('salary', employeeData.salary);
        formData.append('DOB', formattedDOB);
        formData.append('profile', employeeData.profile);

        fetch('http://localhost:3001/employees/add', {
            method: 'POST',
            body: formData,
        })
        .then((response) => response.json())
        .then((data) => {
            console.log('Employee added:', data);
            navigate('/dashboard');
        
             Swal.fire({
            icon: 'success',
            title: 'Employee Added Successfully!',
            showConfirmButton: false,
            timer: 1500, // Automatically close the alert after 1.5 seconds
        }); 
        })
        .catch((error) => console.error('Error adding employee:', error));
    };

    return (
        <div>
            <h2>Add Employee</h2>
            <form>
                <label>
                    First Name:
                    <input
                        type="text"
                        name="first_name"
                        value={employeeData.first_name}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Last Name:
                    <input
                        type="text"
                        name="last_name"
                        value={employeeData.last_name}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Department:
                    <input
                        type="text"
                        name="department"
                        value={employeeData.department}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Salary:
                    <input
                        type="text"
                        name="salary"
                        value={employeeData.salary}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Date of Birth:
                    <input
                        type="date"
                        name="DOB"
                        value={employeeData.DOB}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Profile Image:
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageUpload}
                    />
                </label>
                <br />
                <button type="button" onClick={handleAddEmployee}>
                    Add Employee
                </button>
            </form>
        </div>
    );
}

export default AddEmployeePage;
