import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useForm, Controller } from 'react-hook-form';

function UpdateEmployeePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const userRole = parseInt(localStorage.getItem('userRole'), 10);
  const imageInputRef = useRef()
  const { control, handleSubmit, setValue, formState: { errors } } = useForm();
  
  const [image, setImage] = useState(null);

  useEffect(() => {
    fetchEmployeeData();
  }, []);

  const fetchEmployeeData = () => {
    fetch(`http://localhost:3001/employees/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error fetching employee data');
        }
        return response.json();
      })
      .then((data) => {
        Object.keys(data).forEach((key) => {
          if (key === 'role') {
            setValue(key, data[key] === 1);
          } else {
            setValue(key, data[key]);
          }
        });
      })
      .catch((error) => {
        console.error('Error fetching employee data:', error);
      });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleUpdateEmployee = (data) => {
    const formData = new FormData();
    Object.keys(data).forEach((key) => {
      if (key === 'role') {
        formData.append(key, data[key] ? 1 : 0);
      } else {
        formData.append(key, data[key]);
      }
    });

    if (image) {
      formData.append('profile', image);
    }

    const apiUrl = userRole === 1
      ? `http://localhost:3001/employees/${id}` // For employees
      : `http://localhost:3001/employees/${id}`; // For admins

    fetch(apiUrl, {
      method: 'PUT',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error updating employee');
        }
        return response.json();
      })
      .then(() => {
        navigate('/dashboard');
        Swal.fire({
          icon: 'success',
          title: 'Employee Updated Successfully!',
          showConfirmButton: false,
          timer: 2500,
        });
      })
      .catch((error) => {
        console.error('Error updating employee:', error);
      });
  };

  return (
    <div>
      <h2>Update Employee</h2>
      <form onSubmit={handleSubmit(handleUpdateEmployee)}>
        <div>
          <label>First Name:</label>
          <Controller
            name="first_name"
            control={control}
            rules={{ required: 'This field is required.' }}
            defaultValue=""
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.first_name && <p className="error-message">{errors.first_name.message}</p>}
        </div>
        <div>
          <label>Last Name:</label>
          <Controller
            name="last_name"
            control={control}
            rules={{ required: 'This field is required.' }}
            defaultValue=""
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.last_name && <p className="error-message">{errors.last_name.message}</p>}
        </div>
        <div>
          <label>Email:</label>
          <Controller
            name="email"
            control={control}
            rules={{ required: 'This field is required.' }}
            defaultValue=""
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.email && <p className="error-message">{errors.email.message}</p>}
        </div>
        <div>
          <label>Password:</label>
          <Controller
            name="password"
            control={control}
            rules={{ required: 'This field is required.' }}
            defaultValue=""
            render={({ field }) => (
              <input type="password" {...field} />
            )}
          />
          {errors.password && <p className="error-message">{errors.password.message}</p>}
        </div>
        {userRole === 1 && (
          <div>
            <label>Department:</label>
            <Controller
              name="department"
              control={control}
              rules={{ required: 'This field is required.' }}
              defaultValue=""
              render={({ field }) => (
                <input type="text" {...field} />
              )}
            />
            {errors.department && <p className="error-message">{errors.department.message}</p>}
          </div>
        )}
        {userRole === 1 && (
          <div>
            <label>Salary:</label>
            <Controller
              name="salary"
              control={control}
              rules={{ required: 'This field is required.' }}
              defaultValue=""
              render={({ field }) => (
                <input type="text" {...field} />
              )}
            />
            {errors.salary && <p className="error-message">{errors.salary.message}</p>}
          </div>
        )}
        <div>
          <label>Role:</label>
          {userRole === 1 && (
            <Controller
              name="role"
              control={control}
              defaultValue={false}
              render={({ field }) => (
                <input type="checkbox" {...field} />
              )}
            />
          )}
        </div>
        <input
          type="file"
          accept="image/*"
          ref={imageInputRef}
          onChange={handleImageChange}
        />
        <button type="submit">Update Employee</button>
      </form>
    </div>
  );
}

export default UpdateEmployeePage;
