function EmployeeDetailsPage() {
  const { employeeId } = useParams(); // Get the employeeId from the URL
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
    // Make a fetch request to your API using the employeeId
    fetch(`http://localhost:3001/employees/${employeeId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data); // Update the component state with employee details
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
      });
  }, [employeeId]);

  // Render the employee details once they are fetched
  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails.name}</p>
          <p>Employee ID: {employeeDetails.employeeId}</p>
          {/* Add more details as needed */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
