import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function EmployeeDetailsPage() {
  const navigate = useNavigate();
  const employeeId = localStorage.getItem('employeeId'); // Retrieve employee ID from local storage
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
    if (!employeeId) {
      // Handle the case where the employee ID is not found in local storage
      console.error('Employee ID not found in local storage');
      navigate('/'); // Redirect to the login page or handle it differently
      return;
    }

    fetch(`http://localhost:3001/employees/${employeeId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data);
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
      });
  }, [employeeId]);

  const clickUpdateEmployee = () => {
    navigate(`/update-employee/${employeeId}`);
  };

  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails[0].first_name} {employeeDetails[0].last_name}</p>
          <p>Department: {employeeDetails[0].department}</p>
          <p>Salary: {employeeDetails[0].salary}</p>
          <p>DOB: {new Date(employeeDetails[0].DOB).toLocaleDateString()}</p>
          <div>
            <img
              src={employeeDetails[0].image}
              alt={`Profile of ${employeeDetails[0].first_name} ${employeeDetails[0].last_name}`}
            />
          </div>
        </div>
      ) : (
        <p>Loading... sry employee is not found!</p>
      )}
    </div>
  );
}

export default EmployeePage;
