import React, { useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
//import { useForm, Controller } from 'react-hook-form'; // Import react-hook-form
//import './LoginPage.css';

function UpdateEmployeePage() {
    const { id } = useParams();
    const navigate = useNavigate();
   // const { control, handleSubmit, formState: { errors } } = useForm();


    const [employeeData, setEmployeeData] = useState({
        first_name: '',
        last_name: '',
        department: '',
        salary: '',
    });

    const [image, setImage] = useState(null);
    const imageInputRef = useRef();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEmployeeData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
    };

    const handleUpdateEmployee = () => {
        const formData = new FormData();
        formData.append('first_name', employeeData.first_name);
        formData.append('last_name', employeeData.last_name);
        formData.append('department', employeeData.department);
        formData.append('salary', employeeData.salary);
        if (image) {
            formData.append('profile', image);
        }

        fetch(`http://localhost:3001/employees/${id}`, {
            method: 'PUT',
            body: formData,
        })
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Error updating employee');
                }
                return response.json();
            })
            .then((data) => {
                console.log('Employee updated:', data);
                navigate('/dashboard');

                Swal.fire({
                    icon: 'success',
                    title: 'Employee Updated Successfully!',
                    showConfirmButton: false,
                    timer: 2500,
                });
            })
            .catch((error) => {
                console.error('Error updating employee:', error);
            });
    };

   return (
    <div>
      <h2>Update Employee</h2>
      <div>
        <form onSubmit={handleSubmit(handleUpdateEmployee)}>
          <label>
            First Name:
            <Controller
              name="first_name"
              control={control}
              defaultValue={employeeData.first_name}
              rules={{ required: 'FirstName field is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.first_name && (
              <p className="error">{errors.first_name.message}</p>
            )}
          </label>
          <br />
          <label>
            Last Name:
            <Controller
              name="last_name"
              control={control}
              defaultValue={employeeData.last_name}
              rules={{ required: 'LastName field is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.last_name && (
              <p className="error">{errors.last_name.message}</p>
            )}
          </label>
          <br />
          <label>
            Department:
            <Controller
              name="department"
              control={control}
              defaultValue={employeeData.department}
              rules={{ required: 'Department field is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.department && (
              <p className="error">{errors.department.message}</p>
            )}
          </label>
          <br />
          <label>
            Salary:
            <Controller
              name="salary"
              control={control}
              defaultValue={employeeData.salary}
              rules={{ required: 'Salary field is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.salary && (
              <p className="error">{errors.salary.message}</p>
            )}
          </label>
          <br />
          <label>
            Profile Image:
            <Controller
              name="profile"
              control={control}
              defaultValue={null}
              rules={{ required: 'Image field is required.' }}
              render={({ field }) => (
                <input
                  type="file"
                  accept="image/*"
                  ref={(ref) => {
                    field.ref(ref);
                    imageInputRef.current = ref;
                  }}
                  onChange={(e) => {
                    field.onChange(e.target.files[0]);
                  }}
                />
              )}
            />
            {errors.profile && (
              <p className="error">{errors.profile.message}</p>
            )}
          </label>
          <br />
          <button type="submit">Update Employee</button>
        </form>
      </div>
    </div>
  );
}

export default UpdateEmployeePage;
