import React, { useState } from 'react';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState(null); // Role state

  const handleLogin = async () => {
    // Send a POST request to your server's login endpoint with email and password
    const response = await fetch('http://localhost:3002/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    if (response.ok) {
      const data = await response.json();
      if (data.success) {
        // Set the user's role in the state
        setRole(data.role);
      } else {
        // Handle login failure (e.g., show an error message)
        console.error('Login failed:', data.message);
      }
    } else {
      // Handle server error (e.g., show an error message)
      console.error('Server error:', response.status);
    }
  };

  // Conditional rendering based on the role state
  if (role === null) {
    return (
      <div>
        <h1>Login</h1>
        <form>
          <div>
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </form>
      </div>
    );
  } else if (role === 0) {
    // Redirect to employee details page
    window.location.href = '/employeedetails';
    return null;
  } else if (role === 1) {
    // Redirect to dashboard page
    window.location.href = '/dashboard';
    return null;
  } else {
    // Handle unknown role or other scenarios
    return <div>Unknown role</div>;
  }
}

export default Login;
