import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';

function EmployeePage() {
  const { employeeId } = useParams();
  const navigate = useNavigate();
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
    // Get the employee ID from local storage if available
    const storedEmployeeId = localStorage.getItem('employeeId');

    if (storedEmployeeId && storedEmployeeId === employeeId) {
      // Use the stored employee ID if it matches the current route
      fetchEmployeeDetails(storedEmployeeId);
    } else {
      // Otherwise, fetch employee details from the API
      fetchEmployeeDetails(employeeId);
    }
  }, [employeeId]);

  const fetchEmployeeDetails = (id) => {
    fetch(`http://localhost:3001/employees/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data);
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
        Swal.fire({
          icon: 'error',
          title: 'Error',
          text: 'An error occurred while fetching employee details',
        });
      });
  };

  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails.first_name} {employeeDetails.last_name}</p>
          <p>Department: {employeeDetails.department}</p>
          <p>Salary: {employeeDetails.salary}</p>
          <p>DOB: {new Date(employeeDetails.DOB).toLocaleDateString()}</p>
          <div>
            <img
              src={employeeDetails.image}
              alt={`Profile of ${employeeDetails.first_name} ${employeeDetails.last_name}`}
            />
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default EmployeePage;
