import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

function UpdateEmployeePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const userRole = localStorage.getItem('userRole');
  const isLimitedForm = userRole === '0';

  const [employeeData, setEmployeeData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    department: '',
    salary: '',
    role: '',
    image: null,
  });

  useEffect(() => {
    fetchEmployeeData();
  }, []);

  const fetchEmployeeData = () => {
    fetch(`http://localhost:3001/employees/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error fetching employee data');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeData(data);
      })
      .catch((error) => {
        console.error('Error fetching employee data:', error);
      });
  };

  const handleUpdateEmployee = () => {
    const updateData = {
      first_name: employeeData.first_name,
      last_name: employeeData.last_name,
      email: employeeData.email,
      password: employeeData.password,
    };

    if (!isLimitedForm) {
      updateData.department = employeeData.department;
      updateData.salary = employeeData.salary;
      updateData.role = employeeData.role;
      // Include other properties as needed
    }

    const formData = new FormData();
    formData.append('userRole', userRole);
    for (const key in updateData) {
      formData.append(key, updateData[key]);
    }

    // Include image if needed
    if (employeeData.image) {
      formData.append('image', employeeData.image);
    }

    fetch(`http://localhost:3001/employees/update-employee/${id}`, {
      method: 'PUT',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error updating employee');
        }
        return response.json();
      })
      .then(() => {
        navigate('/dashboard');
        Swal.fire({
          icon: 'success',
          title: 'Employee Updated Successfully!',
          showConfirmButton: false,
          timer: 2500,
        });
      })
      .catch((error) => {
        console.error('Error updating employee:', error);
      });
  };

  return (
    <div>
      <h2>Update Employee</h2>
      <form>
        <div>
          <label>First Name:</label>
          <input type="text" value={employeeData.first_name} onChange={(e) => setEmployeeData({ ...employeeData, first_name: e.target.value })} />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" value={employeeData.last_name} onChange={(e) => setEmployeeData({ ...employeeData, last_name: e.target.value })} />
        </div>
        <div>
          <label>Email:</label>
          <input type="text" value={employeeData.email} onChange={(e) => setEmployeeData({ ...employeeData, email: e.target.value })} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={employeeData.password} onChange={(e) => setEmployeeData({ ...employeeData, password: e.target.value })} />
        </div>
        {!isLimitedForm && (
          <>
            <div>
              <label>Department:</label>
              <input type="text" value={employeeData.department} onChange={(e) => setEmployeeData({ ...employeeData, department: e.target.value })} />
            </div>
            <div>
              <label>Salary:</label>
              <input type="text" value={employeeData.salary} onChange={(e) => setEmployeeData({ ...employeeData, salary: e.target.value })} />
            </div>
            <div>
              <label>Role:</label>
              <input type="text" value={employeeData.role} onChange={(e) => setEmployeeData({ ...employeeData, role: e.target.value })} />
            </div>
          </>
        )}
        {!isLimitedForm && (
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setEmployeeData({ ...employeeData, image: e.target.files[0] })}
          />
        )}
        <button onClick={handleUpdateEmployee}>Update Employee</button>
      </form>
    </div>
  );
}

export default UpdateEmployeePage;
