import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './DashboardPage.css';

function DashboardPage() {
    const [employeeDetails, setEmployeeDetails] = useState([]);
    useEffect(() => {
        fetch('http://localhost:3001/employees')
            .then(response => response.json())
            .then(data => setEmployeeDetails(data))
            .catch(error => console.error('Error getting employee details:', error));
    }, []);

    const navigate = useNavigate();

    const handleAddEmployee = () => {
        navigate('/add-employee-details'); // Navigate to the Add Employee Details page
    };

    return (
        <div className="dashboard-container">
            <h1>WELCOME</h1>
            <h2>Employees List</h2>
            <ul className="employee-list">
                {employeeDetails.map(employee => (
                    <li key={employee.id} className="employee-item">
                        {/* Display employee information */}
                        <p>{employee.first_name} {employee.last_name}</p>
                        <p>Department: {employee.department}</p>
                        <p>Salary: {employee.salary}</p>
                    </li>
                ))}
            </ul>
            <button className="add-button" onClick={handleAddEmployee}>
                Add Employee
            </button>
        </div>
    );
}

export default DashboardPage;
