function DashboardPage() {
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
        // Fetch the list of employees, including image data
        fetch('http://localhost:3001/employees', {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}` // Include JWT token
            }
        })
        .then(response => response.json())
        .then(data => setEmployees(data))
        .catch(error => console.error('Error fetching employees:', error));
    }, []);

    return (
        <div>
            <h2>Dashboard</h2>
            <Link to="/add">Add Employee</Link>
            <table>
                <thead>
                    <tr>
                        <th>Image</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Department</th>
                        <th>Salary</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map(employee => (
                        <tr key={employee.id}>
                            <td>
                                <img
                                    src={`http://localhost:3001/images/${employee.image}`} // Adjust the path to your image route
                                    alt={`${employee.first_name} ${employee.last_name}`}
                                    style={{ width: '100px', height: 'auto' }}
                                />
                            </td>
                            <td>{employee.first_name}</td>
                            <td>{employee.last_name}</td>
                            <td>{employee.department}</td>
                            <td>{employee.salary}</td>
                            <td>
                                <Link to={`/update/${employee.id}`}>Update</Link>
                                <Link to={`/delete/${employee.id}`}>Delete</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default DashboardPage;
