import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function EmployeePage() {
  const { employeeId } = useParams();
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
    // Fetch the employee details based on the employeeId from the URL
    fetch(`http://localhost:3001/employees/1`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data);
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
      });
  }, [employeeId]);

  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails[0].first_name} {employeeDetails[0].last_name}</p>
          <p>Department: {employeeDetails[0].department}</p>
          <p>Salary: {employeeDetails[0].salary}</p>
          <p>DOB: {new Date(employeeDetails[0].DOB).toLocaleDateString()}</p>
          <div>
            <img
              src={employeeDetails[0].image}
              alt={`Profile of ${employeeDetails[0].first_name} ${employeeDetails[0].last_name}`}
            />
          </div>
        </div>
      ) : (
        <p>Loading...   sry employee is not found!</p>
      )}
    </div>
  );
}

export default EmployeePage;
