import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';
import Modal from 'react-modal';
import { useForm, Controller } from 'react-hook-form';
import Swal from 'sweetalert2';

function LoginPage() {
  const navigate = useNavigate();
  const { handleSubmit, control, formState: { errors }, reset } = useForm();

  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [isRegisterModalOpen, setIsRegisterModalOpen] = useState(false);

  const openLoginModal = () => {
    setIsLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setIsLoginModalOpen(false);
    reset(); // Clear form input values
  };

  const openRegisterModal = () => {
    setIsRegisterModalOpen(true);
  };

  const closeRegisterModal = () => {
    setIsRegisterModalOpen(false);
    reset(); // Clear form input values
  };

  const handleLogin = async (data) => {
    try {
      // Send a POST request to the login endpoint with data.username and data.password
      const response = await fetch('http://localhost:3001/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          password: data.password,
        }),
      });

      if (response.ok) {
        const responseData = await response.json();
        localStorage.setItem('token', responseData.token);
        console.log('JWT Token is:', responseData.token);
        navigate('/dashboard');
        closeLoginModal();
      } else {
        console.error('Login failed');
        Swal.fire({
          icon: 'error',
          title: 'Login Failed',
          text: 'Invalid username or password',
        });
      }
    } catch (error) {
      console.error('Error login:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to log in',
      });
    }
  };

  const handleRegister = async (data) => {
    try {
      // Send a POST request to the registration endpoint with data.username, data.email, data.password, and data.confirmPassword
      const response = await fetch('http://localhost:3001/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          email: data.email,
          password: data.password,
          confirmPassword: data.confirmPassword,
        }),
      });

      if (response.ok) {
        Swal.fire({
          icon: 'success',
          title: 'Registration Successful',
          text: 'You can now log in with your new account',
        });
        closeRegisterModal();
      } else {
        console.error('Registration failed');
        Swal.fire({
          icon: 'error',
          title: 'Registration Failed',
          text: 'Failed to register. Please try again later.',
        });
      }
    } catch (error) {
      console.error('Error registering:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to register',
      });
    }
  };

  return (
    <div className="login-container">
      <h2>Welcome</h2>
      <button onClick={openLoginModal}>Login</button>
      <button onClick={openRegisterModal}>Register</button>

      {/* Login Modal */}
      <Modal
        isOpen={isLoginModalOpen}
        onRequestClose={closeLoginModal}
        contentLabel="Login Modal"
      >
        <h2>Login</h2>
        <form onSubmit={handleSubmit(handleLogin)}>
          <Controller
            name="username"
            control={control}
            defaultValue=""
            rules={{
              required: 'Username is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Username"
                  {...field}
                />
                {errors.username && (
                  <p className="error">{errors.username.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="password"
            control={control}
            defaultValue=""
            rules={{
              required: 'Password is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Password"
                  {...field}
                />
                {errors.password && (
                  <p className="error">{errors.password.message}</p>
                )}
              </>
            )}
          />
          <button type="submit">Login</button>
        </form>
        <button onClick={closeLoginModal}>Close</button>
      </Modal>

      {/* Register Modal */}
      <Modal
        isOpen={isRegisterModalOpen}
        onRequestClose={closeRegisterModal}
        contentLabel="Register Modal"
      >
        <h2>Register</h2>
        <form onSubmit={handleSubmit(handleRegister)}>
          <Controller
            name="username"
            control={control}
            defaultValue=""
            rules={{
              required: 'Username is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Username"
                  {...field}
                />
                {errors.username && (
                  <p className="error">{errors.username.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="email"
            control={control}
            defaultValue=""
            rules={{
              required: 'Email is required',
              pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                message: 'Invalid email address',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Email"
                  {...field}
                />
                {errors.email && (
                  <p className="error">{errors.email.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="password"
            control={control}
            defaultValue=""
            rules={{
              required: 'Password is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Password"
                  {...field}
                />
                {errors.password && (
                  <p className="error">{errors.password.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="confirmPassword"
            control={control}
            defaultValue=""
            rules={{
              required: 'Confirm Password is required',
              validate: (value) => value === control.getValues('password') || 'Passwords do not match',
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Confirm Password"
                  {...field}
                />
                {errors.confirmPassword && (
                  <p className="error">{errors.confirmPassword.message}</p>
                )}
              </>
            )}
          />
          <button type="submit">Register</button>
        </form>
        <button onClick={closeRegisterModal}>Close</button>
      </Modal>
    </div>
  );
}

export default LoginPage;
