import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function DashboardPage() {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    // Fetch all employees when the component mounts
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('/:id'); // Replace with your API endpoint
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees', error);
    }
  };

  const deleteEmployee = async (employeeId) => {
    try {
      await axios.delete(`/delete/:id ${employeeId}`); // Replace with your API endpoint
      // After deleting, fetch the updated list of employees
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee', error);
    }
  };

  return (
    <div>
      <h2>Dashboard</h2>
      <Link to="/adds">Add Employee</Link>
      <ul>
        {employees.map((employee) => (
          <li key={employee.id}>
            {employee.first_name} {employee.last_name} ({employee.department})
            <button onClick={() => deleteEmployee(employee.id)}>Delete</button>
            <Link to={`/edit-employee/${employee.id}`}>Edit</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DashboardPage;
