import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useForm, Controller } from 'react-hook-form';

function UpdateEmployeePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const userRole = parseInt(localStorage.getItem('userRole'), 10);

  const [employeeData, setEmployeeData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    department: '',
    salary: '',
    role: 0,
  });

  const [image, setImage] = useState(null);
  const imageInputRef = useRef();

  const isRequired = (value) => {
    return !!value || 'This field is required.';
  };

  const { control, handleSubmit, formState: { errors } } = useForm();

  useEffect(() => {
    fetchEmployeeData();
  }, []);

  const fetchEmployeeData = () => {
    fetch(`http://localhost:3001/employees/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error fetching employee data');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeData(data);
      })
      .catch((error) => {
        console.error('Error fetching employee data:', error);
      });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleUpdateEmployee = (data) => {
    const formData = new FormData();
    formData.append('first_name', data.first_name);
    formData.append('last_name', data.last_name);
    formData.append('email', data.email);
    formData.append('password', data.password);

    if (userRole === 1) {
      // Admin-specific data
      formData.append('department', data.department);
      formData.append('salary', data.salary);
    }
    formData.append('role', data.role);

    if (image) {
      formData.append('profile', image);
    }

    const apiUrl = userRole === 0
      ? `http://localhost:3001/employees/emp/${id}`  // For employees
      : `http://localhost:3001/employees/${id}`;      // For admins

    fetch(apiUrl, {
      method: 'PUT',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error updating employee');
        }
        return response.json();
      })
      .then(() => {
        navigate('/dashboard');
        Swal.fire({
          icon: 'success',
          title: 'Employee Updated Successfully!',
          showConfirmButton: false,
          timer: 2500,
        });
      })
      .catch((error) => {
        console.error('Error updating employee:', error);
      });
  };

  return (
    <div>
      <h2>Update Employee</h2>
      <form onSubmit={handleSubmit(handleUpdateEmployee)}>
        <div>
          <label>First Name:</label>
          <Controller
            name="first_name"
            control={control}
            rules={{ validate: isRequired }}
            defaultValue={employeeData.first_name}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.first_name && <p className="error-message">{errors.first_name.message}</p>}
        </div>
        <div>
          <label>Last Name:</label>
          <Controller
            name="last_name"
            control={control}
            rules={{ validate: isRequired }}
            defaultValue={employeeData.last_name}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.last_name && <p className="error-message">{errors.last_name.message}</p>}
        </div>
        <div>
          <label>Email:</label>
          <Controller
            name="email"
            control={control}
            rules={{ validate: isRequired }}
            defaultValue={employeeData.email}
            render={function({ field }) {
              return <input type="text" {...field} />
            }}
          />
          {errors.email && <p className="error-message">{errors.email.message}</p>}
        </div>
        <div>
          <label>Password:</label>
          <Controller
            name="password"
            control={control}
            rules={{ validate: isRequired }}
            defaultValue={employeeData.password}
            render={({ field }) => (
              <input type="password" {...field} />
            )}
          />
          {errors.password && <p className="error-message">{errors.password.message}</p>}
        </div>
        <div>
          <label>Department:</label>
          {userRole === 1 && (
            <Controller
              name="department"
              control={control}
              rules={{ validate: isRequired }}
              defaultValue={employeeData.department}
              render={({ field }) => (
                <input type="text" {...field} />
              )}
            />
          )}
          {errors.department && <p className="error-message">{errors.department.message}</p>}
        </div>
        <div>
          <label>Salary:</label>
          {userRole === 1 && (
            <Controller
              name="salary"
              control={control}
              rules={{ validate: isRequired }}
              defaultValue={employeeData.salary}
              render={({ field }) => (
                <input type="text" {...field} />
              )}
            />
          )}
          {errors.salary && <p className="error-message">{errors.salary.message}</p>}
        </div>
        <div>
          <div>
            <label>Role:</label>
           {userRole === 1 && (
            <Controller
              name="role"
              control={control}
              defaultValue={employeeData.role === 1}
              render={({ field }) => (
                <input type="checkbox" {...field} />
              )}
            />
        )}
          </div>
          <input
            type="file"
            accept="image/*"
            ref={imageInputRef}
            onChange={handleImageChange}
          />
          <button type="submit">Update Employee</button>
        </div>
      </form>
    </div>
  );
}

export default UpdateEmployeePage;
