import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    // Send a POST request to your server's login endpoint with email and password
    const response = await fetch('http://localhost:3001/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    if (response.ok) {
      const data = await response.json();
      if (data.success) {
        // Successful login, navigate based on the user's role
        if (data.role === 0) {
          navigate('/employeedetails'); // Redirect to employee details page
        } else if (data.role === 1) {
          navigate('/dashboard'); // Redirect to dashboard page
        }
      } else {
        // Login failed, handle error (e.g., show an error message)
        console.error('Login failed:', data.message);
      }
    } else {
      // Server error, handle error (e.g., show an error message)
      console.error('Server error:', response.status);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <form>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="button" onClick={handleLogin}>
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
