import React, { useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useForm, Controller } from 'react-hook-form';
import './LoginPage.css';

function UpdateEmployeePage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const { control, handleSubmit, formState: { errors } } = useForm(); 
  const [employeeData, setEmployeeData] = useState({
    first_name: '',
    last_name: '',
    department: '',
    salary: '',
    email: '',
    password: '',
    role: false,
  });

  const [image, setImage] = useState(null);
  const imageInputRef = useRef();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleUpdateEmployee = (data) => {
    const formData = new FormData();
    formData.append('first_name', data.first_name);
    formData.append('last_name', data.last_name);
    formData.append('department', data.department);
    formData.append('salary', data.salary);
    if (image) {
      formData.append('profile', image);
    }
    formData.append('email', data.email);
    formData.append('password', data.password);
    formData.append('role', data.role);

    fetch(`http://localhost:3001/employees/${id}`, {
      method: 'PUT',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error updating employee');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Employee updated:', data);
        navigate('/dashboard');

        Swal.fire({
          icon: 'success',
          title: 'Employee Updated Successfully!',
          showConfirmButton: false,
          timer: 2500,
        });
      })
      .catch((error) => {
        console.error('Error updating employee:', error);
      });
  };

  return (
    <div>
      <h2>Update Employee</h2>
      <div>
        <form onSubmit={handleSubmit(handleUpdateEmployee)}>
          <label>
            First Name:
            <Controller
              name="first_name"
              control={control}
              defaultValue={employeeData.first_name}
              rules={{ required: 'First Name is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.first_name && (
              <p className="error">{errors.first_name.message}</p>
            )}
          </label>
          <br />
          <label>
            Last Name:
            <Controller
              name="last_name"
              control={control}
              defaultValue={employeeData.last_name}
              rules={{ required: 'Last Name is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.last_name && (
              <p className="error">{errors.last_name.message}</p>
            )}
          </label>
          <br />
          <label>
            Department:
            <Controller
              name="department"
              control={control}
              defaultValue={employeeData.department}
              rules={{ required: 'Department is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.department && (
              <p className="error">{errors.department.message}</p>
            )}
          </label>
          <br />
          <label>
            Salary:
            <Controller
              name="salary"
              control={control}
              defaultValue={employeeData.salary}
              rules={{ required: 'Salary is required.' }}
              render={({ field }) => <input {...field} />}
            />
            {errors.salary && (
              <p className="error">{errors.salary.message}</p>
            )}
          </label>
          <br />
          <label>
            Profile Image:
            <Controller
              name="profile"
              control={control}
              defaultValue={null}
              render={({ field }) => (
                <input
                  type="file"
                  accept="image/*"
                  ref={(ref) => {
                    field.ref(ref);
                    imageInputRef.current = ref;
                    ref && ref.addEventListener('change', handleImageChange);
                  }}
                />
              )}
            />
          </label>
         <label>
          Email:
          <Controller
            name="email"
            control={control}
            defaultValue=""
            rules={{
              required: 'Email is required.',
              pattern: {
                value: /\S+@\S+\.\S+/,
                message: 'Invalid email format',
              },
            }}
            render={({ field }) => <input {...field} />}
          />
        </label>
        {errors.email && <p className="error">{errors.email.message}</p>}
        <br />
        <label>
          Password:
          <Controller
            name="password"
            control={control}
            defaultValue=""
            rules={{
              required: 'Password is required.',
              minLength: {
                value: 6,
                message: 'Password must be at least 6 characters.',
              },
            }}
            render={({ field }) => <input type="password" {...field} />}
          />
        </label>
        {errors.password && <p className="error">{errors.password.message}</p>}
        <br />
        <label>
          <Controller
            name="role"
            control={control}
            defaultValue={false}
            render={({ field }) => (
              <div>
                <input type="checkbox" {...field} />
                Make an Admin
              </div>
            )}
          />
        </label>
        <br />
          <br />
          <button type="submit">Update Employee</button>
        </form>
      </div>
    </div>
  );
}

export default UpdateEmployeePage;
