import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function DashboardPage() {
    const [employeeDetails, setEmployeeDetails] = useState([]);
    useEffect(() => {
        fetch('http://localhost:3001/employees')
            .then(response => response.json())
            .then(data => setEmployeeDetails(data))
            .catch(error => console.error('Error fetching employee details:', error));
    }, []);

    const navigate = useNavigate();

    const handleAddEmployee = () => {
        navigate('/add-employee'); // Navigate to the "Add Employee" page
    };

    const handleUpdateEmployee = (employeeId) => {
        navigate(`/update-employee/${employeeId}`); // Navigate to the "Update Employee" page with employee ID
    };

    const handleDeleteEmployee = (employeeId) => {
        navigate(`/delete-employee/${employeeId}`); // Navigate to the "Delete Employee" page with employee ID
    };

    return (
        <div>
            <h2>Employee List</h2>
            <ul>
                {employeeDetails.map(employee => (
                    <li key={employee.id}>
                        {employee.first_name} {employee.last_name} ({employee.department}) - Salary: {employee.salary}
                        <button onClick={() => handleUpdateEmployee(employee.id)}>Update Employee here!</button>
                        <button onClick={() => handleDeleteEmployee(employee.id)}>Delete Employee here!</button>
                    </li>
                ))}
            </ul>
            <button onClick={handleAddEmployee}>Add Employee here!</button>
        </div>
    );
}

export default DashboardPage;
