import React, { useState, useEffect } from 'react';

function EmployeePage() {
  // Retrieve the employee_id from localStorage
  const storedEmployeeId = localStorage.getItem('employee_id');
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
    if (!storedEmployeeId) {
      // Handle the case where employee_id is not found in localStorage
      console.error('Employee ID not found in localStorage');
      return;
    }

    // Fetch employee details using the employee_id from localStorage
    fetch(`http://localhost:3001/employees/${storedEmployeeId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data);
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
      });
  }, [storedEmployeeId]);

  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>ID: {employeeDetails.id}</p>
          <p>Name: {employeeDetails.first_name} {employeeDetails.last_name}</p>
          <p>Department: {employeeDetails.department}</p>
          <p>Salary: {employeeDetails.salary}</p>
          <p>DOB: {new Date(employeeDetails.DOB).toLocaleDateString()}</p>
          <p>Role: {employeeDetails.role}</p>
          <p>Email: {employeeDetails.email}</p>
          {/* You can display other details as needed */}
          <div>
            <img
              src={employeeDetails.image}
              alt={`Profile of ${employeeDetails.first_name} ${employeeDetails.last_name}`}
            />
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default EmployeePage;
