import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function EmployeePage() {
  const { employee_id } = useParams();
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
    // Retrieve the stored employee_id from local storage
    const storedEmployeeId = localStorage.getItem('employee_id');

    // Check if the stored employee_id matches the one from the URL
    if (storedEmployeeId === employee_id) {
      // Fetch employee details using the stored employee_id
      fetch(`http://localhost:3001/employees/${employee_id}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Failed to fetch employee details');
          }
          return response.json();
        })
        .then((data) => {
          setEmployeeDetails(data);
        })
        .catch((error) => {
          console.error('Error fetching employee details:', error);
        });
    } else {
      // Handle the case where the stored employee_id doesn't match the URL
      console.error('Stored employee_id does not match the URL.');
    }
  }, [employee_id]);

  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails.first_name} {employeeDetails.last_name}</p>
          <p>Department: {employeeDetails.department}</p>
          <p>Salary: {employeeDetails.salary}</p>
          <p>DOB: {new Date(employeeDetails.DOB).toLocaleDateString()}</p>
          <div>
            <img
              src={employeeDetails.image}
              alt={`Profile of ${employeeDetails.first_name} ${employeeDetails.last_name}`}
            />
          </div>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default EmployeePage;
