import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async () => {
    // Send a POST request to your server's login endpoint with email and password
    const response = await fetch('http://localhost:3002/login', { // Update the URL accordingly
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    if (response.ok) {
      const data = await response.json();
      if (data.success) {
        // Successful login, redirect based on the user's role
        if (data.role === 0) {
          history.push('/employeedetails'); // Redirect to employee details page
        } else if (data.role === 1) {
          history.push('/dashboard'); // Redirect to dashboard page
        }
      } else {
        // Login failed, handle error (e.g., show an error message)
        console.error('Login failed:', data.message);
      }
    } else {
      // Server error, handle error (e.g., show an error message)
      console.error('Server error:', response.status);
    }
  };

  return (
    <div>
      {/* Your login form JSX here */}
    </div>
  );
}

export default LoginPage;
