import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css'; // Include your existing styles
 // Include the new CSS based on the provided design

function LoginPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const LoginAction = () => {
    if (username === 'gowtham' && password === '123') {
      navigate('/dashboard');
    }
  };

  return (
    <div className="login-container block-cube"> {/* Add block-cube class */}
      <h2>Login</h2>
      <div className="control">
        <input
          type="text"
          placeholder="Enter Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div className="control">
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button className="btn" onClick={LoginAction}>
        Login
      </button>
    </div>
  );
}

export default LoginPage;
