import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EmployeeDetailsPage() {
  const navigate = useNavigate(); 
  const { employeeId } = useParams(); 
  const [employeeDetails, setEmployeeDetails] = useState(null);

  useEffect(() => {
   
    fetch(`http://localhost:3001/employees/${employeeId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data);
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
      });
  }, [employeeId]);

  const clickUpdateEmployee = () => {
    navigate(`/update-employee/${employeeId}`);
  };

 
  return (
    <div>
      <h2>Employee Details</h2>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails.name}</p>
          <p>Employee ID: {employeeDetails.employeeId}</p>
          <button
            className="update-button"
            onClick={clickUpdateEmployee}
          >
            Update
          </button>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default EmployeeDetailsPage;
