import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Modal from 'react-modal';

function EmployeePage() {
  const { employeeId: urlEmployeeId } = useParams();
  const [employeeId, setEmployeeId] = useState(urlEmployeeId);
  const [employeeDetails, setEmployeeDetails] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    // Fetch the employee details based on the employeeId from the server
    fetch(`http://localhost:3001/employees/1`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeDetails(data);
      })
      .catch((error) => {
        console.error('Error fetching employee details:', error);
      });
  }, [employeeId]);

  const handleEmployeeIdChange = (e) => {
    setEmployeeId(e.target.value);
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Moved the return statement inside the component function
  return (
    <div>
      <h2>Employee Details</h2>
      <label>
        Enter Employee ID:
        <input
          type="text"
          value={employeeId}
          onChange={handleEmployeeIdChange}
        />
        <button onClick={openModal}>Show Details</button>
      </label>
      {employeeDetails ? (
        <div>
          <p>Name: {employeeDetails.first_name} {employeeDetails.last_name}</p>
          <p>Department: {employeeDetails.department}</p>
          <p>Salary: {employeeDetails.salary}</p>
          <p>DOB: {new Date(employeeDetails.DOB).toLocaleDateString()}</p>
          <div>
            <img
              src={employeeDetails.image}
              alt={`Profile of ${employeeDetails.first_name} ${employeeDetails.last_name}`}
            />
          </div>
        </div>
      ) : (
        <p>Loading... Sorry, employee details not found.</p>
      )}

      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Employee Details Modal"
      >
        <h2>Employee Details</h2>
        {employeeDetails ? (
          <div>
            <p>Name: {employeeDetails.first_name} {employeeDetails.last_name}</p>
            <p>Department: {employeeDetails.department}</p>
            <p>Salary: {employeeDetails.salary}</p>
            <p>DOB: {new Date(employeeDetails.DOB).toLocaleDateString()}</p>
            <div>
              <img
                src={employeeDetails.image}
                alt={`Profile of ${employeeDetails.first_name} ${employeeDetails.last_name}`}
              />
            </div>
          </div>
        ) : (
          <p>Loading... Sorry, employee details not found.</p>
        )}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
}

export default EmployeePage;
