import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';
import Modal from 'react-modal';
import { useForm, Controller } from 'react-hook-form';
import Swal from 'sweetalert2';

function LoginPage() {
  const navigate = useNavigate();
  const {
    handleSubmit,
    control,
    formState: { errors },
    reset,
    getValues,
  } = useForm();

  const [isLoginPopOpen, setIsLoginPopOpen] = useState(false);
  const [isEmployeeLoginPopOpen, setIsEmployeeLoginPopOpen] = useState(false);
  const [isRegisterPopOpen, setIsRegisterPopOpen] = useState(false);

  const openAdminLoginPop = () => {
    setIsLoginPopOpen(true);
  };

  const closeAdminLoginPop = () => {
    setIsLoginPopOpen(false);
    reset();
  };

  const openEmployeeLoginPop = () => {
    setIsEmployeeLoginPopOpen(true);
  };

  const closeEmployeeLoginPop = () => {
    setIsEmployeeLoginPopOpen(false);
    reset();
  };

  const openRegisterPop = () => {
    setIsRegisterPopOpen(true);
  };

  const closeRegisterPop = () => {
    setIsRegisterPopOpen(false);
    reset();
  };

  const validateConfirmPassword = (value) => {
    const passwordValue = getValues('password');
    return value === passwordValue || 'Passwords do not match';
  };

  const handleAdminLogin = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          password: data.password,
        }),
      });

      if (response.ok) {
        const responseData = await response.json();
        localStorage.setItem('token', responseData.token);
        console.log('JWT Token is:', responseData.token);
        navigate('/dashboard');
        closeAdminLoginPop();
      } else {
        console.error('Login failed');
        Swal.fire({
          icon: 'error',
          title: 'Login Failed',
          text: 'Invalid username or password',
        });
      }
    } catch (error) {
      console.error('Error login:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to log in',
      });
    }
  };

 const handleEmployeeLogin = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/employee-login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          employeeId: data.employeeId,
          password: data.password,
        }),
      });

      if (response.ok) {
        const responseData = await response.json();
        localStorage.setItem('token', responseData.token);
        console.log('JWT Token is:', responseData.token);
        navigate('/dashboard'); // Redirect to the dashboard or another page upon successful login
        closeEmployeeLoginPop();
      } else {
        console.error('Employee login failed');
        Swal.fire({
          icon: 'error',
          title: 'Employee Login Failed',
          text: 'Invalid employee ID or password',
        });
      }
    } catch (error) {
      console.error('Error employee login:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to log in as an employee',
      });
    }
  };

  const handleRegister = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          email: data.email,
          password: data.password,
          confirmPassword: data.confirmPassword,
        }),
      });

      if (response.ok) {
        Swal.fire({
          icon: 'success',
          title: 'Registration Successful',
          text: 'You can now log in with your new account',
        });
        closeRegisterPop();
      } else {
        console.error('Registration failed');
        Swal.fire({
          icon: 'error',
          title: 'Registration Failed',
          text: 'Failed to register. Please try again later.',
        });
      }
    } catch (error) {
      console.error('Error registering:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to register',
      });
    }
  };

  return (
    <div className="login-container">
      <h2>Welcome</h2>
      <button onClick={openAdminLoginPop}>Admin Login</button>
      <button onClick={openEmployeeLoginPop}>Employee Login</button>
      <button onClick={openRegisterPop}>Register</button>

      {/* Employee Login */}
      <Modal
        isOpen={isEmployeeLoginPopOpen}
        onRequestClose={closeEmployeeLoginPop}
        contentLabel="Employee Login Modal"
      >
        <h2>Employee Login</h2>
        <form onSubmit={handleSubmit(handleEmployeeLogin)}>
          <Controller
            name="employeeId"
            control={control}
            defaultValue=""
            rules={{
              required: 'Employee ID is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Enter Employee ID"
                  {...field}
                />
                {errors.employeeId && (
                  <p className="error">{errors.employeeId.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="password"
            control={control}
            defaultValue=""
            rules={{
              required: 'Password is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Enter Password"
                  {...field}
                />
                {errors.password && (
                  <p className="error">{errors.password.message}</p>
                )}
              </>
            )}
          />
          <button type="submit">Login</button>
          <button className="close-button" onClick={closeEmployeeLoginPop}>
            Close
          </button>
        </form>
      </Modal>

      {/* Admin Login */}
      <Modal
        isOpen={isLoginPopOpen}
        onRequestClose={closeAdminLoginPop}
        contentLabel="Admin Login Modal"
      >
        <h2>Admin Login</h2>
        <form onSubmit={handleSubmit(handleAdminLogin)}>
          <Controller
            name="adminUsername"
            control={control}
            defaultValue=""
            rules={{
              required: 'Admin Username is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Enter Admin Username"
                  {...field}
                />
                {errors.adminUsername && (
                  <p className="error">{errors.adminUsername.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="adminPassword"
            control={control}
            defaultValue=""
            rules={{
              required: 'Admin Password is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Enter Admin Password"
                  {...field}
                />
                {errors.adminPassword && (
                  <p className="error">{errors.adminPassword.message}</p>
                )}
              </>
            )}
          />
          <button type="submit">Login</button>
          <button className="close-button" onClick={closeAdminLoginPop}>
            Close
          </button>
        </form>
      </Modal>

      {/* Registration */}
      <Modal
        isOpen={isRegisterPopOpen}
        onRequestClose={closeRegisterPop}
        contentLabel="Registration Modal"
      >
        <h2>Register</h2>
        <form onSubmit={handleSubmit(handleRegister)}>
          <Controller
            name="username"
            control={control}
            defaultValue=""
            rules={{
              required: 'Username is required',
              minLength: {
                value: 3,
                message: 'Username should be at least 3 characters',
              },
              maxLength: {
                value: 25,
                message: 'Username should not exceed 25 characters',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Username (3-25 characters)"
                  {...field}
                />
                {errors.username && (
                  <p className="error">{errors.username.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="email"
            control={control}
            defaultValue=""
            rules={{
              required: 'Email is required',
              pattern: {
                value: /^\S+@\S+$/i,
                message: 'Invalid email address',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Enter valid Email"
                  {...field}
                />
                {errors.email && (
                  <p className="error">{errors.email.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="password"
            control={control}
            defaultValue=""
            rules={{
              required: 'Password is required',
              minLength: {
                value: 3,
                message: 'Password should be at least 3 characters long',
              },
              maxLength: {
                value: 25,
                message: 'Password should not exceed 25 characters',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Password (3-25 characters)"
                  {...field}
                />
                {errors.password && (
                  <p className="error">{errors.password.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="confirmPassword"
            control={control}
            defaultValue=""
            rules={{
              required: 'Confirm Password is required',
              validate: validateConfirmPassword,
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Confirm Password (min 6-25 characters)"
                  {...field}
                />
                {errors.confirmPassword && (
                  <p className="error">{errors.confirmPassword.message}</p>
                )}
              </>
            )}
          />
          <button type="submit">Register</button>
          <button className="close-button" onClick={closeRegisterPop}>
            Close
          </button>
        </form>
      </Modal>
    </div>
  );
}

export default LoginPage;
