import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function DashboardPage() {
    const [employeeDetails, setEmployeeDetails] = useState([]);
    const [newEmployee, setNewEmployee] = useState({
        first_name: '',
        last_name: '',
        department: '',
        salary: ''
    });

    const navigate = useNavigate();

    useEffect(() => {
        fetch('localhost:3001/employees') // Proxy handles the actual URL
            .then(response => response.json())
            .then(data => setEmployeeDetails(data))
            .catch(error => console.error('Error fetching employee details:', error));
    }, []);

    const handleAddEmployee = () => {
        fetch('localhost:3001/adds', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newEmployee)
        })
        .then(response => response.json())
        .then(data => {
            console.log('Employee added:', data);
            // Fetch updated employee details after adding
            fetch('/employees')
                .then(response => response.json())
                .then(updatedData => setEmployeeDetails(updatedData))
                .catch(error => console.error('Error fetching updated employee details:', error));
        })
        .catch(error => console.error('Error adding employee:', error));
    };

    const handleAddAndNavigate = () => {
        handleAddEmployee();
        navigate('/add-employee'); // Navigate to the new page
    };

    return (
        <div>
            <h2>Employee Details</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Department</th>
                        <th>Salary</th>
                    </tr>
                </thead>
                <tbody>
                    {employeeDetails.map(employee => (
                        <tr key={employee.id}>
                            <td>{employee.id}</td>
                            <td>{employee.first_name}</td>
                            <td>{employee.last_name}</td>
                            <td>{employee.department}</td>
                            <td>{employee.salary}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Add Employee</h2>
            <div>
                <input
                    type="text"
                    placeholder="First Name"
                    value={newEmployee.first_name}
                    onChange={e => setNewEmployee({ ...newEmployee, first_name: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Last Name"
                    value={newEmployee.last_name}
                    onChange={e => setNewEmployee({ ...newEmployee, last_name: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Department"
                    value={newEmployee.department}
                    onChange={e => setNewEmployee({ ...newEmployee, department: e.target.value })}
                />
                <input
                    type="text"
                    placeholder="Salary"
                    value={newEmployee.salary}
                    onChange={e => setNewEmployee({ ...newEmployee, salary: e.target.value })}
                />
                <button onClick={handleAddAndNavigate}>Add Employee and Go to Add Employee Page</button>
            </div>
        </div>
    );
}

export default DashboardPage;
