import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

function Login() {
  const { control, handleSubmit, errors } = useForm();
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          const { token, role, employee_id } = result;
          localStorage.setItem('accessToken', token);
          localStorage.setItem('userRole', role);
          localStorage.setItem('employee_id', employee_id);

         console.log("token:",token);

          if (role === 0) {
            Swal.fire('Success', 'You have successfully logged in as an Employee', 'success');
          } else if (role === 1) {
            Swal.fire('Success', 'You have successfully logged in as an Admin', 'success');
          }

          navigate('/dashboard');
        } else {
          Swal.fire('Error', result.message, 'error');
        }
      } else {
        Swal.fire('Server Error', 'There was an issue with the server.', 'error');
      }
    } catch (error) {
      console.error('Error:', error);
      Swal.fire('Error', 'An error occurred while processing your request.', 'error');
    }
  };

  return (
    <div className="login-container">
      <div>
        <h1>Login</h1>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div>
            <label>Email:</label>
            <Controller
              name="email"
              control={control}
              defaultValue=""
              render={({ field }) => (
                <input
                  {...field}
                  type="email"
                  placeholder="Enter email"
                  required
                />
              )}
            />
            {errors.email && <p className="error">Email is required.</p>}
          </div>
          <div>
            <label>Password:</label>
            <Controller
              name="password"
              control={control}
              defaultValue=""
              render={({ field }) => (
                <input
                  {...field}
                  type="password"
                  placeholder="Enter password"
                  required
                />
              )}
            />
            {errors.password && <p className="error">Password is required.</p>}
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
