import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useForm, Controller } from 'react-hook-form';

function AddEmployeePage() {
  const navigate = useNavigate();
  const { handleSubmit, control, errors } = useForm(); // Initialize react-hook-form

  const [profileImage, setProfileImage] = useState(null);

  const onSubmit = (data) => {
    const formattedDOB = new Date(data.DOB).toISOString().split('T')[0];

    const formData = new FormData();
    formData.append('first_name', data.first_name);
    formData.append('last_name', data.last_name);
    formData.append('department', data.department);
    formData.append('salary', data.salary);
    formData.append('DOB', formattedDOB);
    formData.append('profile', profileImage);

    fetch('http://localhost:3001/employees/add', {
      method: 'POST',
      body: formData,
    })
      .then((response) => response.json())
      .then((response) => {
        console.log('Employee added:', response);
        navigate('/dashboard');

        Swal.fire({
          icon: 'success',
          title: 'Employee Added Successfully!',
          showConfirmButton: false,
          timer: 1500,
        });
      })
      .catch((error) => console.error('Error adding employee:', error));
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    setProfileImage(file);
  };

  return (
    <div>
      <h2>Add Employee</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <label>
            First Name:
            <Controller
              name="first_name"
              control={control}
              defaultValue=""
              rules={{ required: 'First Name is required' }}
              render={({ field }) => (
                <>
                  <input type="text" {...field} />
                  <p style={{ color: 'red' }}>{errors.first_name?.message}</p>
                </>
              )}
            />
          </label>
        </div>
        <div>
          <label>
            Last Name:
            <Controller
              name="last_name"
              control={control}
              defaultValue=""
              rules={{ required: 'Last Name is required' }}
              render={({ field }) => (
                <>
                  <input type="text" {...field} />
                  <p style={{ color: 'red' }}>{errors.last_name?.message}</p>
                </>
              )}
            />
          </label>
        </div>
        <div>
          <label>
            Department:
            <Controller
              name="department"
              control={control}
              defaultValue=""
              rules={{ required: 'Department is required' }}
              render={({ field }) => (
                <>
                  <input type="text" {...field} />
                  <p style={{ color: 'red' }}>{errors.department?.message}</p>
                </>
              )}
            />
          </label>
        </div>
        <div>
          <label>
            Salary:
            <Controller
              name="salary"
              control={control}
              defaultValue=""
              rules={{ required: 'Salary is required' }}
              render={({ field }) => (
                <>
                  <input type="text" {...field} />
                  <p style={{ color: 'red' }}>{errors.salary?.message}</p>
                </>
              )}
            />
          </label>
        </div>
        <div>
          <label>
            Date of Birth:
            <Controller
              name="DOB"
              control={control}
              defaultValue=""
              rules={{ required: 'Date of Birth is required' }}
              render={({ field }) => (
                <>
                  <input type="date" {...field} />
                  <p style={{ color: 'red' }}>{errors.DOB?.message}</p>
                </>
              )}
            />
          </label>
        </div>
        <div>
          <label>
            Profile Image:
            <input type="file" name="profile" accept="image/*" onChange={handleImageUpload} />
          </label>
        </div>
        <div>
          <button type="submit">Add Employee</button>
        </div>
      </form>
    </div>
  );
}

export default AddEmployeePage;
