import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddEmployeeDetails() {
    const [employeeData, setEmployeeData] = useState({
        first_name: '',
        last_name: '',
        department: '',
        salary: '',
        dob: '' // Add a DOB field
    });

    const navigate = useNavigate();

    const handleInputChange = event => {
        const { name, value } = event.target;
        setEmployeeData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };
const handleAddEmployee = () => {
    // Fetch existing employees' data from your API
    fetch('http://localhost:3001/employees')
        .then(response => response.json())
        .then(data => {
            // Check if there is an employee with the same details
            const duplicateEmployee = data.find(employee => {
                return (
                   
                    employee.dob === employeeData.dob
                );
            });

            if (duplicateEmployee) {
                alert('Employee with the same details already exists.');
            } else {
                // Perform the add operation if there is no match
                fetch('http://localhost:3001/adds', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(employeeData)
                })
                .then(response => response.json())
                .then(data => {
                    console.log('Employee added:', data);
                    navigate('/dashboard'); // Navigate back to the Dashboard page
                })
                .catch(error => console.error('Error adding employee:', error));
            }
        })
        .catch(error => {
            console.error('Error fetching employees:', error);
        });
};


    return (
        <div>
            <h2>Add Employee Details</h2>
            <form>
                <label>
                    First Name:
                    <input
                        type="text"
                        name="first_name"
                        value={employeeData.first_name}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Last Name:
                    <input
                        type="text"
                        name="last_name"
                        value={employeeData.last_name}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Department:
                    <input
                        type="text"
                        name="department"
                        value={employeeData.department}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Salary:
                    <input
                        type="text"
                        name="salary"
                        value={employeeData.salary}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Date of Birth (DOB):
                    <input
                        type="text"
                        name="dob"
                        value={employeeData.dob}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <button type="button" onClick={handleAddEmployee}>Add Employee</button>
            </form>
        </div>
    );
}

export default AddEmployeeDetails;
