import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function DeleteEmployeePage() {
    const { id } = useParams(); // Get the employee ID from the URL
    const navigate = useNavigate(); // Initialize useNavigate

    const [employeeData, setEmployeeData] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:3001/${id}`)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Employee not found');
                }
                return response.json();
            })
            .then(data => setEmployeeData(data))
            .catch(error => {
                console.error('Error fetching employee details:', error);
                navigate('/dashboard'); // Redirect to dashboard if employee not found
            });
    }, [id, navigate]);

    const handleDeleteEmployee = () => {
        fetch(`http://localhost:3001/delete/${id}`, {
            method: 'DELETE'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error deleting employee');
                }
                console.log('Employee deleted');
                navigate('/dashboard'); // Use useNavigate to navigate
            })
            .catch(error => {
                console.error('Error deleting employee:', error);
            });
    };

    if (!employeeData) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Delete Employee</h2>
            <div>
                <p>Are you sure you want to delete the following employee?</p>
              
                <button onClick={handleDeleteEmployee}>Delete Employee</button>
            </div>
        </div>
    );
}

export default DeleteEmployeePage;
