import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddEmployeePage() {
    const [employeeData, setEmployeeData] = useState({
        first_name: '',
        last_name: '',
        department: '',
        salary: '',
        DOB: '', // Added the DOB field
        image: null, // Added the image field
    });

    const navigate = useNavigate();

    const handleInputChange = event => {
        const { name, value } = event.target;
        setEmployeeData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleImageChange = event => {
        const imageFile = event.target.files[0];
        setEmployeeData(prevData => ({
            ...prevData,
            image: imageFile
        }));
    };

    const handleAddEmployee = () => {
        // Check if all required fields are filled
        if (!employeeData.first_name || !employeeData.last_name || !employeeData.department || !employeeData.salary || !employeeData.DOB) {
            alert('All fields (first_name, last_name, department, salary, DOB) are required.');
            return;
        }

        // Format the DOB as "YYYY-MM-DD"
        const formattedDOB = new Date(employeeData.DOB).toISOString().split('T')[0];
        const dataToSend = new FormData();
        dataToSend.append('first_name', employeeData.first_name);
        dataToSend.append('last_name', employeeData.last_name);
        dataToSend.append('department', employeeData.department);
        dataToSend.append('salary', employeeData.salary);
        dataToSend.append('DOB', formattedDOB);
        dataToSend.append('image', employeeData.image);

        fetch('http://localhost:3001/adds', {
            method: 'POST',
            body: dataToSend
        })
        .then(response => response.json())
        .then(data => {
            console.log('Employee added:', data);
            navigate('/dashboard'); 
        })
        .catch(error => console.error('Error adding employee:', error));
    };

    return (
        <div>
            <h2>Add Employee</h2>
            <form>
                <label>
                    First Name:
                    <input
                        type="text"
                        name="first_name"
                        value={employeeData.first_name}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Last Name:
                    <input
                        type="text"
                        name="last_name"
                        value={employeeData.last_name}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Department:
                    <input
                        type="text"
                        name="department"
                        value={employeeData.department}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Salary:
                    <input
                        type="text"
                        name="salary"
                        value={employeeData.salary}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Date of Birth:
                    <input
                        type="date"
                        name="DOB"
                        value={employeeData.DOB}
                        onChange={handleInputChange}
                    />
                </label>
                <br />
                <label>
                    Image:
                    <input
                        type="file"
                        name="image"
                        accept="image/*"
                        onChange={handleImageChange}
                    />
                </label>
                <br />
                <button type="button" onClick={handleAddEmployee}>Add Employee</button>
            </form>
        </div>
    );
}

export default AddEmployeePage;
