import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './DashboardPage.css';
import { format } from 'date-fns'; 
import jwt_decode from 'jwt-decode';
import Swal from 'sweetalert2';

function DashboardPage() {
    const navigate = useNavigate();
    const [employeeDetails, setEmployeeDetails] = useState([]);
    const [employeeToDelete, setEmployeeToDelete] = useState(null);
    const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);

   useEffect(() => {
    // Fetch all employees and set them in the state
    fetch('http://localhost:3001/employees/all')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch employee details');
        }
        return response.json();
      })
      .then((data) => setEmployeeDetails(data))
      .catch((error) => {
        console.error('Error getting employee details:', error);
      });
  }, []);

    const clickAddEmployee = () => {
        navigate('/add-employee');
    };

    const clickUpdateEmployee = (employeeId) => {
        navigate(`/update-employee/${employeeId}`);
    };

    const clickDeleteEmployee = (employeeId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: 'You are about to delete this employee!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Yes, delete it!',
            cancelButtonText: 'No, cancel',
            reverseButtons: true,
        }).then((result) => {
            if (result.isConfirmed) {
                setEmployeeToDelete(employeeId);
                confirmDelete();
            }
        });
    };

 const confirmDelete = () => {
    if (employeeToDelete) {
        fetch(`http://localhost:3001/employees/${employeeToDelete}`, {
            method: 'DELETE'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Error deleting employee');
                }
                return response.json();
            })
            .then(data => {
                console.log('Employee deleted:', data);
                Swal.fire({
                    title: 'Employee Deleted',
                    text: 'Employee deleted successfully.',
                    icon: 'success',
                });
                setShowDeleteConfirmation(false);

             
                window.location.reload();
            })
            .catch(error => {
                console.error('Error deleting employee:', error);
                Swal.fire({
                    title: 'Error',
                    text: 'An error occurred while deleting the employee.',
                    icon: 'error',
                });
            });
    }
};




    return (
        <div className="dashboard-container">
            <h1>WELCOME</h1>
            <h2>Employees List</h2>
            <ul className="employee-list">
                {employeeDetails.map(employee => {
                    const dob = new Date(employee.DOB);
                    const formattedDOB = format(dob, 'dd/MM/yyyy'); 

                    return (
                        <li key={employee.id} className="employee-item">
                            <div className="employee-image">
                                <img src={employee.image} alt={`Profile of ${employee.first_name} ${employee.last_name}`} />
                            </div>
                            <div className="employee-info">
                                <p className="employee-name">
                                    {employee.first_name} {employee.last_name}
                                </p>
                                <p className="employee-department">
                                    Department: {employee.department}
                                </p>
                                <p className="employee-salary">
                                    Salary: {employee.salary}
                                </p>
                                <p className="employee-dob">
                                    DOB: {formattedDOB}
                                </p>
                            </div>
                            <div className="employee-buttons">
                                <button
                                    className="update-button"
                                    onClick={() => clickUpdateEmployee(employee.id)}
                                >
                                    Update
                                </button>
                                <button
                                    className="delete-button"
                                    onClick={() => clickDeleteEmployee(employee.id)}
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    );
                })}
            </ul>
            {showDeleteConfirmation && (
                <div className="delete-confirmation-modal">
                    <p>Are you sure you want to delete this employee?</p>
                    <div className="modal-buttons">
                        <button onClick={confirmDelete}>Yes</button>
                        <button onClick={() => setShowDeleteConfirmation(false)}>No</button>
                    </div>
                </div>
            )}
            <button className="add-button" onClick={clickAddEmployee}>
                Add Employee
            </button>
        </div>
    );
}

export default DashboardPage;
