import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [employees, setEmployees] = useState([]);
  const [newEmployee, setNewEmployee] = useState({
    first_name: '',
    last_name: '',
    department: '',
    salary: ''
  });

  useEffect(() => {
    fetch('http://localhost:3001/employees') // Replace with your backend endpoint
      .then(response => response.json())
      .then(data => setEmployees(data))
      .catch(error => console.error('Error fetching employees:', error));
  }, []);

  const handleAddEmployee = () => {
    fetch('http://localhost:3001/adds', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newEmployee)
    })
      .then(response => response.json())
      .then(data => {
        if (data.message === 'Employee added successfully') {
          setEmployees([...employees, data.employeesDetails]);
          setNewEmployee({
            first_name: '',
            last_name: '',
            department: '',
            salary: ''
          });
        }
      })
      .catch(error => console.error('Error adding employee:', error));
  };

  return (
    <div className="App">
      <h1>Employee Management</h1>
      <div>
        <h2>Add New Employee</h2>
        <input
          type="text"
          placeholder="First Name"
          value={newEmployee.first_name}
          onChange={e => setNewEmployee({ ...newEmployee, first_name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Last Name"
          value={newEmployee.last_name}
          onChange={e => setNewEmployee({ ...newEmployee, last_name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Department"
          value={newEmployee.department}
          onChange={e => setNewEmployee({ ...newEmployee, department: e.target.value })}
        />
        <input
          type="text"
          placeholder="Salary"
          value={newEmployee.salary}
          onChange={e => setNewEmployee({ ...newEmployee, salary: e.target.value })}
        />
        <button onClick={handleAddEmployee}>Add Employee</button>
      </div>
      <div>
        <h2>Employee Details</h2>
        <ul>
          {employees.map(employee => (
            <li key={employee.id}>
              {employee.first_name} {employee.last_name} ({employee.department}) - Salary: {employee.salary}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
