import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';

function AddEmployeePage() {
  const { control, handleSubmit, formState: { errors } } = useForm();
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    // Validate form data
    if (!data.first_name || !data.last_name || !data.department || !data.salary || !data.DOB || !data.profile) {
      alert('All fields (first_name, last_name, department, salary, DOB, and profile image) are required.');
      return;
    }

    const formattedDOB = new Date(data.DOB).toISOString().split('T')[0];

    const formData = new FormData();
    formData.append('first_name', data.first_name);
    formData.append('last_name', data.last_name);
    formData.append('department', data.department);
    formData.append('salary', data.salary);
    formData.append('DOB', formattedDOB);
    formData.append('profile', data.profile[0]); // Use data.profile[0] to access the selected file

    try {
      const response = await fetch('http://localhost:3001/employees/add', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const responseData = await response.json();
        console.log('Employee added:', responseData);
        navigate('/dashboard');

        Swal.fire({
          icon: 'success',
          title: 'Employee Added Successfully!',
          showConfirmButton: false,
          timer: 1500,
        });
      } else {
        throw new Error('Failed to add employee');
      }
    } catch (error) {
      console.error('Error adding employee:', error);
    }
  };

  return (
    <div>
      <h2>Add Employee</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
          <Controller
            name="first name"
            control={control}
            defaultValue=""
            rules={{
              required: 'firstname is required',
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Enter firstname"
                  {...field}
                />
                {errors.firstname && (
                  <p className="error">{errors.firstname.message}</p>
                )}
              </>
            )}
          />
        <br />
        <label>
          Last Name:
          <Controller
            name="last_name"
            control={control}
            defaultValue=""
            rules={{ required: true }}
            render={({ field }) => <input {...field} />}
          />
        </label>
        {errors.last_name && <p>This field is required.</p>}
        <br />
        <label>
          Department:
          <Controller
            name="department"
            control={control}
            defaultValue=""
            rules={{ required: true }}
            render={({ field }) => <input {...field} />}
          />
        </label>
        {errors.department && <p>This field is required.</p>}
        <br />
        <label>
          Salary:
          <Controller
            name="salary"
            control={control}
            defaultValue=""
            rules={{ required: true }}
            render={({ field }) => <input {...field} />}
          />
        </label>
        {errors.salary && <p>This field is required.</p>}
        <br />
        <label>
          Date of Birth:
          <Controller
            name="DOB"
            control={control}
            defaultValue=""
            rules={{ required: true }}
            render={({ field }) => <input type="date" {...field} />}
          />
        </label>
        {errors.DOB && <p>This field is required.</p>}
        <br />
        <label>
          Profile Image:
          <Controller
            name="profile"
            control={control}
            defaultValue={null}
            rules={{ required: true }}
            render={({ field }) => <input type="file" accept="image/*" onChange={(e) => field.onChange(e.target.files)} />}
          />
        </label>
        {errors.profile && <p>This field is required.</p>}
        <br />
        <button type="submit">Add Employee</button>
      </form>
    </div>
  );
}

export default AddEmployeePage;
