import React, { useState } from 'react';
import Modal from 'react-modal';
import Swal from 'sweetalert2';

function LoginPage() {
  const [isEmployeeLoginPopOpen, setIsEmployeeLoginPopOpen] = useState(false);
  const [employeeId, setEmployeeId] = useState('');
  const [employeeDetails, setEmployeeDetails] = useState([]);
  const [isEmployeeDetailsOpen, setIsEmployeeDetailsOpen] = useState(false);

  const openEmployeeLoginPop = () => {
    setIsEmployeeLoginPopOpen(true);
  };

  const closeEmployeeLoginPop = () => {
    setIsEmployeeLoginPopOpen(false);
    setEmployeeId('');
    setEmployeeDetails(null);
  };

  const openEmployeeDetails = () => {
    setIsEmployeeDetailsOpen(true);
  };

  const closeEmployeeDetails = () => {
    setIsEmployeeDetailsOpen(false);
  };

  const handleEmployeeLogin = async () => {
    try {
      if (!employeeId) {
        Swal.fire({
          icon: 'error',
          title: 'Employee ID Required',
          text: 'Please enter your employee ID',
        });
        return;
      }

      const response = await fetch(`http://localhost:3001/employees/${employeeId}`, {
        method: 'GET',
      });

      if (response.ok) {
        const responseData = await response.json();
        setEmployeeDetails(responseData);
        openEmployeeDetails();
      } else {
        console.error('Employee login failed');
        Swal.fire({
          icon: 'error',
          title: 'Employee Login Failed',
          text: 'Invalid employee ID',
        });
      }
    } catch (error) {
      console.error('Error employee login:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to log in as an employee',
      });
    }
  };

  return (
    <div className="login-container">
      <h2>Welcome</h2>
      <button onClick={openEmployeeLoginPop}>Employee-Login</button>

      {/* Employee Login */}
      <Modal
        isOpen={isEmployeeLoginPopOpen}
        onRequestClose={closeEmployeeLoginPop}
        contentLabel="Employee Login Modal"
      >
        <h2>Employee Login</h2>
        <input
          type="text"
          placeholder="Enter Employee ID"
          value={employeeId}
          onChange={(e) => setEmployeeId(e.target.value)}
        />
        <button onClick={handleEmployeeLogin}>Login</button>
        <button className="close-button" onClick={closeEmployeeLoginPop}>
          Close
        </button>
      </Modal>

      {/* Employee Details */}
      <Modal
        isOpen={isEmployeeDetailsOpen}
        onRequestClose={closeEmployeeDetails}
        contentLabel="Employee Details Modal"
      >
        <h2>Employee Details</h2>
        {employeeDetails ? (
          <div>
            <p>Name: {employeeDetails.first_name} {employeeDetails.last_name}</p>
            <p>Department: {employeeDetails.department}</p>
            <p>Salary: {employeeDetails.salary}</p>
            <p>DOB: {new Date(employeeDetails.DOB).toLocaleDateString()}</p>
            <div>
              <img src={employeeDetails.image} alt="Profile" width="150" height="150" />
            </div>
          </div>
        ) : (
          <p>No employee details available</p>
        )}
        <button className="close-button" onClick={closeEmployeeDetails}>
          Close
        </button>
      </Modal>
    </div>
  );
}

export default LoginPage;
