import React, { useState } from 'react';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:3001/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          // Check user's role and redirect accordingly
          if (data.role === 0) {
            // Employee role, redirect to /employeePage
            window.location.href = '/employeePage';
          } else if (data.role === 1) {
            // Admin role, redirect to /dashboard
            window.location.href = '/dashboard';
          }
          // You can also store the user ID and role in localStorage here
        } else {
          // Login failed, handle error (e.g., show an error message)
          console.error('Login failed:', data.message);
        }
      } else {
        // Server error, handle error (e.g., show an error message)
        console.error('Server error:', response.status);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <form>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="button" onClick={handleLogin}>
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
