import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './DashboardPage.css';
import { format } from 'date-fns';

function DashboardPage() {
    const [employeeDetails, setEmployeeDetails] = useState([]);
    useEffect(() => {
        fetch('http://localhost:3001/employees')
            .then(response => response.json())
            .then(data => setEmployeeDetails(data))
            .catch(error => console.error('Error getting employee details:', error));
    }, []);

    const navigate = useNavigate();

    const handleAddEmployee = () => {
        navigate('/add-employee'); 
    };

    const handleUpdateEmployee = (employeeId) => {
        navigate(`/update-employee/${employeeId}`); 
    };

    const handleDeleteEmployee = (employeeId) => {
        navigate(`/delete-employee/${employeeId}`);
    }; 

    return (
        <div className="dashboard-container">
            <h1>WELCOME</h1>
            <h2>Employees List</h2>
            <ul className="employee-list">
                {employeeDetails.map(employee => {
                    const dob = new Date(employee.DOB);
                    const formattedDOB = format(dob, 'dd/MM/yyyy');

                    return (
                        <li key={employee.id} className="employee-item">
                            <div className="employee-info">
                                <p className="employee-name">
                                    {employee.first_name} {employee.last_name}
                                </p>
                                <p className="employee-department">
                                    Department: {employee.department}
                                </p>
                                <p className="employee-salary">
                                    Salary: {employee.salary}
                                </p>
                                <p className="employee-dob">
                                    DOB: {formattedDOB}
                                </p>
                            </div>
                            <div className="employee-image">
    <img
        src={`image.jpg;base64,${employee.image}`}
        alt={`${employee.first_name} ${employee.last_name}`}
    />
</div>

                            <div className="employee-buttons">
                                <button
                                    className="update-button"
                                    onClick={() => handleUpdateEmployee(employee.id)}
                                >
                                    Update
                                </button>
                                <button
                                    className="delete-button"
                                    onClick={() => handleDeleteEmployee(employee.id)}
                                >
                                    Delete
                                </button>
                            </div>
                        </li>
                    );
                })}
            </ul>
            <button className="add-button" onClick={handleAddEmployee}>
                Add Employee
            </button>
        </div>
    );
}

export default DashboardPage;
