import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import { useForm, Controller } from 'react-hook-form';

function UpdateEmployeePage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [employeeData, setEmployeeData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    department: '',
    salary: '',
    role: 0,
  });

  const [image, setImage] = useState(null);
  const imageInputRef = useRef();

  const isRequired = (value) => {
    return !!value || 'This field is required.';
  };

  const { control, handleSubmit, setValue, getValues, formState: { errors } } = useForm();

  useEffect(() => {
    fetchEmployeeData();
  }, []);

  const fetchEmployeeData = () => {
    fetch(`http://localhost:3001/employees/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error fetching employee data');
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeData(data);
      })
      .catch((error) => {
        console.error('Error fetching employee data:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleRoleChange = (e) => {
    const { name, checked } = e.target;
    setEmployeeData((prevData) => ({ ...prevData, [name]: checked ? 1 : 0 }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleUpdateEmployee = () => {
    const formData = new FormData();
    formData.append('first_name', employeeData.first_name);
    formData.append('last_name', employeeData.last_name);
    formData.append('email', employeeData.email);
    formData.append('password', employeeData.password);
    formData.append('department', employeeData.department);
    formData.append('salary', employeeData.salary);
    formData.append('role', employeeData.role);
    if (image) {
      formData.append('profile', image);
    }

    fetch(`http://localhost:3001/employees/${id}`, {
      method: 'PUT',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error updating employee');
        }
        return response.json();
      })
      .then(() => {
        navigate('/dashboard');
        Swal.fire({
          icon: 'success',
          title: 'Employee Updated Successfully!',
          showConfirmButton: false,
          timer: 2500,
        });
      })
      .catch((error) => {
        console.error('Error updating employee:', error);
      });
  };

  return (
    <div>
      <h2>Update Employee</h2>
      <form onSubmit={handleSubmit(handleUpdateEmployee)}>
        <div>
          <label>First Name:</label>
          <Controller
            name="first_name"
            control={control}
            rules={{ validate: isRequired }}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.first_name && <p className="error-message">{errors.first_name.message}</p>}
        </div>
        <div>
          <label>Last Name:</label>
          <Controller
            name="last_name"
            control={control}
            rules={{ validate: isRequired }}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.last_name && <p className="error">{errors.last_name.message}</p>}
        </div>
        <div>
          <label>Email:</label>
          <Controller
            name="email"
            control={control}
            rules={{ validate: isRequired }}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.email && <p className="error">{errors.email.message}</p>}
        </div>
        <div>
          <label>Password:</label>
          <Controller
            name="password"
            control={control}
            rules={{ validate: isRequired }}
            render={({ field }) => (
              <input type="password" {...field} />
            )}
          />
          {errors.password && <p className="error">{errors.password.message}</p>}
        </div>
        <div>
          <label>Department:</label>
          <Controller
            name="department"
            control={control}
            rules={{ validate: isRequired }}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.department && <p className="error">{errors.department.message}</p>}
        </div>
        <div>
          <label>Salary:</label>
          <Controller
            name="salary"
            control={control}
            rules={{ validate: isRequired }}
            render={({ field }) => (
              <input type="text" {...field} />
            )}
          />
          {errors.salary && <p className="error">{errors.salary.message}</p>}
        </div>
        <label>
          Role:
          <input
            type="checkbox"
            name="role"
            checked={employeeData.role === 1}
            onChange={handleRoleChange}
          />
        </label>
        <input
          type="file"
          accept="image/*"
          ref={imageInputRef}
          onChange={handleImageChange}
        />
        <button type="submit">Update Employee</button>
      </form>
    </div>
  );

}

export default UpdateEmployeePage;
