import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';
import Modal from 'react-modal';
import { useForm, Controller } from 'react-hook-form';
import Swal from 'sweetalert2';

function LoginPage() {
  const navigate = useNavigate();
  const {
    handleSubmit,
    control,
    formState: { errors },
    reset,
    getValues,
  } = useForm();

  const [isLoginPopOpen, setIsLoginPopOpen] = useState(false);
  const [isRegisterPopOpen, setIsRegisterPopOpen] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [loginOption, setLoginOption] = useState(null);

  const openLoginPop = (option) => {
    setLoginOption(option);
    setIsLoginPopOpen(true);
  };

  const closeLoginPop = () => {
    setIsLoginPopOpen(false);
    reset();
  };

  const openRegisterPop = () => {
    setIsRegisterPopOpen(true);
  };

  const closeRegisterPop = () => {
    setIsRegisterPopOpen(false);
    reset();
  };

  const validateConfirmPassword = (value) => {
    const passwordValue = getValues('password');
    return value === passwordValue || 'Passwords do not match';
  };

  const handleLogin = async (data) => {
    try {
      let url = 'http://localhost:3001/auth/login';

      // Check login option (admin or employee)
      if (loginOption === 'admin') {
        url = 'http://localhost:3001/auth/admin-login';
      }

      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          password: data.password,
        }),
      });

      if (response.ok) {
        const responseData = await response.json();
        localStorage.setItem('token', responseData.token);
        console.log('JWT Token is:', responseData.token);
        navigate('/dashboard');
        closeLoginPop();
      } else {
        console.error('Login failed');
        Swal.fire({
          icon: 'error',
          title: 'Login Failed',
          text: 'Invalid username or password',
        });
      }
    } catch (error) {
      console.error('Error login:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to log in',
      });
    }
  };

  const handleRegister = async (data) => {
    try {
      const response = await fetch('http://localhost:3001/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          email: data.email,
          password: data.password,
          confirmPassword: data.confirmPassword,
        }),
      });

      if (response.ok) {
        Swal.fire({
          icon: 'success',
          title: 'Registration Successful',
          text: 'You can now log in with your new account',
        });
        closeRegisterPop();
      } else {
        console.error('Registration failed');
        Swal.fire({
          icon: 'error',
          title: 'Registration Failed',
          text: 'Failed to register. Please try again later.',
        });
      }
    } catch (error) {
      console.error('Error registering:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to register',
      });
    }
  };

  return (
    <div className="login-container">
      <h2>Welcome</h2>
      <button onClick={() => openLoginPop('employee')}>Employee Login</button><br />
      <button onClick={() => openLoginPop('admin')}>Admin Login</button><br />
      <button onClick={openRegisterPop}>Register</button>

      {/* Login */}
      <Modal
        isOpen={isLoginPopOpen}
        onRequestClose={closeLoginPop}
        contentLabel="Login Modal"
      >
        <h2>{loginOption === 'admin' ? 'Admin Login' : 'Employee Login'}</h2>
        <form onSubmit={handleSubmit(handleLogin)}>
          {/* Existing login form code */}
        </form>
        <button className="close-button" onClick={closeLoginPop}>
          Close
        </button>
      </Modal>

      {/* Registration */}
      <Modal
        isOpen={isRegisterPopOpen}
        onRequestClose={closeRegisterPop}
        contentLabel="Registration Modal"
      >
        <h2>Register</h2>
        <form onSubmit={handleSubmit(handleRegister)}>
          <Controller
            name="username"
            control={control}
            defaultValue=""
            rules={{
              required: 'Username is required',
              minLength: {
                value: 3,
                message: 'Username should be at least 3 characters',
              },
              maxLength: {
                value: 25,
                message: 'Username should not exceed 25 characters',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Username (3-25 characters)"
                  {...field}
                />
                {errors.username && (
                  <p className="error">{errors.username.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="email"
            control={control}
            defaultValue=""
            rules={{
              required: 'Email is required',
              pattern: {
                value: /^\S+@\S+$/i,
                message: 'Invalid email address',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="text"
                  placeholder="Enter valid Email"
                  {...field}
                />
                {errors.email && (
                  <p className="error">{errors.email.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="password"
            control={control}
            defaultValue=""
            rules={{
              required: 'Password is required',
              minLength: {
                value: 3,
                message: 'Password should be at least 3 characters long',
              },
              maxLength: {
                value: 25,
                message: 'Password should not exceed 25 characters',
              },
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Password (3-25 characters)"
                  {...field}
                />
                {errors.password && (
                  <p className="error">{errors.password.message}</p>
                )}
              </>
            )}
          />
          <Controller
            name="confirmPassword"
            control={control}
            defaultValue=""
            rules={{
              required: 'Confirm Password is required',
              validate: validateConfirmPassword,
            }}
            render={({ field }) => (
              <>
                <input
                  type="password"
                  placeholder="Confirm Password (min 6-25 characters)"
                  {...field}
                />
                {errors.confirmPassword && (
                  <p className="error">{errors.confirmPassword.message}</p>
                )}
              </>
            )}
          />
          <button type="submit">Register</button>
          <button className="close-button" onClick={closeRegisterPop}>
            Close
          </button>
        </form>
      </Modal>
    </div>
  );
}

export default LoginPage;
