import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';
import Swal from 'sweetalert2';
import { useForm, Controller } from 'react-hook-form';

function LoginPage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const { handleSubmit, control, formState: { errors } } = useForm();

  const handleLogin = async (data) => {
    try {
      // Send a POST request to the login endpoint with data.username and data.password
      const response = await fetch('http://localhost:3001/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          password: data.password,
        }),
      });

      if (response.ok) {
        const responseData = await response.json();
        localStorage.setItem('token', responseData.token);
        console.log('JWT Token is:', responseData.token);
        navigate('/dashboard');
      } else {
        console.error('Login failed');
        Swal.fire({
          icon: 'error',
          title: 'Login Failed',
          text: 'Invalid username or password',
        });
      }
    } catch (error) {
      console.error('Error login:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to log in',
      });
    }
  };

  // Function for handling registration
  const handleRegister = async (data) => {
    try {
      // Send a POST request to the registration endpoint with data.username, data.email, data.password, and data.confirmPassword
      const response = await fetch('http://localhost:3001/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: data.username,
          email: data.email,
          password: data.password,
          confirmPassword: data.confirmPassword,
        }),
      });

      if (response.ok) {
        Swal.fire({
          icon: 'success',
          title: 'Registration Successful',
          text: 'You can now log in with your new account',
        });
      } else {
        console.error('Registration failed');
        Swal.fire({
          icon: 'error',
          title: 'Registration Failed',
          text: 'Failed to register. Please try again later.',
        });
      }
    } catch (error) {
      console.error('Error registering:', error);
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while trying to register',
      });
    }
  };
  return (
    <div className="login-container">
      <h2>Welcome</h2>
      <button onClick={() => Swal.fire({
        title: 'Login',
        html:
          '<input id="swal-input1" class="swal2-input" placeholder="Username">' +
          '<input type="password" id="swal-input2" class="swal2-input" placeholder="Password">',
        focusConfirm: false,
        preConfirm: () => {
          const username = Swal.getPopup().querySelector('#swal-input1').value;
          const password = Swal.getPopup().querySelector('#swal-input2').value;
          handleLogin({ username, password });
        },
      })}>Login</button>
      <button onClick={() => Swal.fire({
        title: 'Register',
        html:
          '<input id="swal-input1" class="swal2-input" placeholder="Username">' +
          '<input id="swal-input2" class="swal2-input" placeholder="Email">' +
          '<input type="password" id="swal-input3" class="swal2-input" placeholder="Password">' +
          '<input type="password" id="swal-input4" class="swal2-input" placeholder="Confirm Password">',
        focusConfirm: false,
        preConfirm: () => {
          const username = Swal.getPopup().querySelector('#swal-input1').value;
          const email = Swal.getPopup().querySelector('#swal-input2').value;
          const password = Swal.getPopup().querySelector('#swal-input3').value;
          const confirmPassword = Swal.getPopup().querySelector('#swal-input4').value;
          handleRegister({ username, email, password, confirmPassword });
        },
      })}>Register</button>

      {/* The rest of your login form with react-hook-form */}
      <form onSubmit={handleSubmit(handleLogin)}>
        <Controller
          name="username"
          control={control}
          defaultValue=""
          rules={{
            required: 'Username is required',
          }}
          render={({ field }) => (
            <>
              <input
                type="text"
                placeholder="Username"
                {...field}
              />
              {errors.username && (
                <p className="error">{errors.username.message}</p>
              )}
            </>
          )}
        />
        {/* Add similar Controller components for email, password, and confirmPassword */}
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
